import{_ as r,r as t,o as u,f as d,w as l,d as e,h as o,b as n}from"./index-cbda5572.js";const i={name:"jianzhi"},p=n("h1",null,[n("span",null,[n("a",{href:"../"},[n("strong",null,"县城特色")])]),o(" / 中阳剪纸，艺术传承")],-1),h=n("br",null,null,-1),f=n("br",null,null,-1),m=n("br",null,null,-1),v=n("br",null,null,-1),x=n("br",null,null,-1);function b(w,$,B,N,V,g){const s=t("v-card-text"),_=t("v-card"),c=t("v-col"),a=t("v-row");return u(),d(a,null,{default:l(()=>[e(c,{cols:"12",xl:"6",lg:"8",md:"10",sm:"12",xs:"12",class:"ma-auto"},{default:l(()=>[p,h,e(_,null,{default:l(()=>[e(s,null,{default:l(()=>[o(" 剪纸艺术在中国有着深厚的文化底蕴。它不仅是一种传统的手工艺品，也是一种民间艺术形式。中阳剪纸作为中国剪纸的重要流派之一，具有独特的风格和技巧，深受人们的喜爱和推崇。在中阳，不同地区的剪纸也都各不相同、各具特色。受地理环境以及民风民俗的影响，有些地区的剪纸风格细腻、古朴典雅；而有些地区则粗犷、浑厚、刚健，这些不同的风格丰富了中阳剪纸的特色。 "),f,o(" 中阳剪纸与当地传统民俗文化血肉相连。它以中阳当地民俗信仰、岁时节令、人生礼仪、神话传说为主要表现内容，其中既有以鱼、蛙、蛇、兔为主题的装饰纹样，也有配合岁时节令、人生礼仪的民俗剪纸，还有以民间神话为题材的剪纸作品。中阳剪纸多以红纸剪成，体现着喜庆、热烈的民俗气氛，有时也根据风俗习惯，运用紫、黑、黄、绿、蓝等彩色纸剪制作品。中阳剪纸主要出自中阳农村劳动妇女之手，剪纸是她们日常生活的一项重要内容，是她们审美情趣和聪明才智的集中表现。中阳剪纸富有浓郁的山野泥土气息和原始艺术的质朴美感，生动形象地记录了劳动妇女的理想和追求。其技艺的传承关系一般是自发的，亦有以家族方式传承的。 "),m,o(" 2006年5月20日，中阳剪纸经国务院批准列入第一批国家级非物质文化遗产名录。2007年6月5日，经文化部确定，山西省中阳县的王计汝为该文化遗产项目代表性传承人，并被列入第一批国家级非物质文化遗产项目226名代表性传承人名单。2009年中阳剪纸又被联合国教科文组织列为世界遗产名录。 "),v,o(" 中阳剪纸现在依然面临诸多问题。很多中阳剪纸的传承人年龄偏大，如何能够将这一门手艺一直传承下去是现在面临的第一大问题。另外，中阳剪纸的特点是纯手工制作，这一特点使得它的成本相对较高，其销量并不是很大，这是中阳剪纸面临的第二大问题。多年来，针对这些问题，历届县委、县政府非常重视，将中阳县传统民俗剪纸作为全县文化产业发展的重头来抓，设立专项资金，成立剪纸协会，建成民俗剪纸博物馆，支持开办文化产业开发公司。全县组建了县、乡、村三级剪纸培训网络，举办了面向全国的“首届中阳剪纸艺术节”。为了将中阳剪纸更好地传承下去，中阳县评选命名了两批剪纸代表性传承人和传承新秀，给予一定的生活补贴。截至目前，中阳剪纸有国家级传承人1人、省级传承人2人、市级传承人16人、县级传承人19人、传承新秀20人。实践队所参观的中阳县华艺民俗文化发展有限公司是市级传承人武小汾创建的。那里所展出的所有剪纸都是由武小汾和她的团队制作的。通过这样的形式，既使得剪纸艺术得到传承，又能在一定程度上带动就业，可谓一举两得。作为中阳县的定点帮扶单位，北京航空航天大学校园里就有很多中阳剪纸的艺术品。2023届毕业生收到的毕业礼盒中，就有武小汾带领中阳县庞家会村村民与剪纸协会学员专门制作的特色剪纸。 "),x]),_:1})]),_:1})]),_:1})]),_:1})}const k=r(i,[["render",b]]);export{k as default};
