import{p as d,a as x,b as g}from"./pics-249b8887.js";import{_ as f,r as l,o as e,f as c,w as o,d as y,b as i,t as S,c as a,g as v,i as n,F as A}from"./index-64802b71.js";const b={name:"xulele",data(){return{title:"访谈——许乐乐",contents:[{type:"pics",title:"中阳县阳坡村人，患唇裂伴智力障碍",imgSrc:[["src/assets/interview/xulele-1.jpg"]],placedAt:["below"],content:["——乐乐的爷爷育有两子。长子结婚后生下姐姐和乐乐，平日里都在县城打工，一年仅回村两次。后来乐乐的父母婚姻破裂。乐乐妈妈带着姐姐去外地读书。乐乐则被留在村里，由爷爷奶奶照顾。而乐乐爸爸基本不过问乐乐的养育，每个月只是给爷爷200元。奶奶去世之后，便只剩爷爷和乐乐相依为命：学期间两人住在县城，爷爷照顾乐乐的日常起居；假期间，乐乐和爷爷回阳坡村居住，一同经营家中田地。爷爷的次子也在县城工作，结婚生子后在县城定居，偶尔回家帮帮忙。乐乐患唇裂伴智力障碍，幼年在北京航空航天大学的支持下前往北京进行治疗，先后进行了5场手术，花销约为10万，如今乐乐恢复得非常好，能看出疤痕但不是很明显，平日里也能和他人进行基本的沟通。但由于术后治疗和恢复需要时间，乐乐8岁才开始上小学，2023年9月开学后上四年级。<br>——据了解，爷爷靠低保维持基本生活，低保标准提高后，每个月爷爷可以拿到５００元左右。乐乐还有残疾证，每月有一定的残疾补助。此外，乐乐家中有三十几亩地，乐乐和爷爷两人很难独自完成全部的种植工作，因此将部分田地租给他人，留下十几亩自己耕种，每年约有一万多元的收入。乐乐家的收入在阳坡村处于中下水平，但在村委会与政策的帮扶下，二人也能勉强维持生活。<br>——爷爷平时照顾乐乐的日常起居，空闲下来就靠刷短视频消磨时间。等乐乐放假回到家里后，除了照顾乐乐、种田卖玉米以外，有时候在家看电视。乐乐回村后经常在村中四处闲逛，由于缺少合适的玩伴，有时只能自己独自玩耍。爷爷在放假时通常每隔一两周会去县城买买菜，或者处理些其他事务。"]}]}},components:{plaintext:d,pics:x,pic:g}},k=i("br",null,null,-1);function w(h,B,C,N,s,V){const r=l("plaintext"),p=l("pic"),m=l("pics"),u=l("v-col"),_=l("v-row");return e(),c(_,null,{default:o(()=>[y(u,{cols:"12",xl:"6",lg:"8",md:"10",sm:"12",xs:"12",class:"ma-auto"},{default:o(()=>[i("h1",null,S(s.title),1),k,(e(!0),a(A,null,v(s.contents,t=>(e(),a("div",null,[t.type==="plaintext"?(e(),c(r,{key:0,content:t.content,title:t.title},null,8,["content","title"])):n("",!0),t.type==="pic"?(e(),c(p,{key:1,content:t.content,title:t.title,imgSrc:t.imgSrc,placedAt:t.placedAt},null,8,["content","title","imgSrc","placedAt"])):n("",!0),t.type==="pics"?(e(),c(m,{key:2,content:t.content,title:t.title,imgSrc:t.imgSrc,placedAt:t.placedAt},null,8,["content","title","imgSrc","placedAt"])):n("",!0)]))),256))]),_:1})]),_:1})}const j=f(b,[["render",w]]);export{j as default};
